mapper.insert:
INSERT
	INTO MEMBERS
	VALUES (
		#{member_id}
		, #{member_pass}
		, #{member_name}
		, #{member_email}
		, #{member_birth}
		, now() 			-- {member_date}
		, #{member_phone}
		, #{member_status} -- ENUM 타입 에서 String 타입으로 변경 
		, #{member_agree_marketing}
		, #{member_withdrawl}
		, #{member_type} -- CHECK 제약 조건 
		, #{member_like_genre, jdbcType=VARCHAR} -- NULL
		, 1
		, 'BRONZE'
	)

INSERT
	INTO MEMBERS
	VALUES (
		#{member_phone}
		, #{member_pass}
		, #{member_name}
		, #{member_email}
		, #{member_birth}
		, now() 			-- {member_date}
		, #{member_phone}
		, '활동'
		, 0					-- 0이 뭐였지
		, #{member_withdrawl}
		, '비회원'
		, '선택 안함' -- NULL
		, 1
		, 'NONE'
	)

mapper.select:
SELECT member_pass
	FROM MEMBERS
	WHERE member_name = #{member_name}
		AND member_id = #{member_phone}

mapper.update:
UPDATE MEMBERS
SET 
    grade_name = #{grade_name}
WHERE
    member_id = #{member_id}

UPDATE MEMBERS
SET
	member_status = #{member_status}
WHERE
	member_id = #{member_id}
