mapper.update:
		UPDATE
			CS
			SET 
  				cs_content = #{cs_content}
				
  					, cs_file_real = #{cs_file_real}
  				
  			WHERE cs_num = #{cs_num}

mapper.select:
		SELECT member_pass
  			FROM MEMBERS
  			WHERE member_id = #{member_id}

mapper.update:
UPDATE
	MEMBERS
	SET
		
			member_pass = #{member_pass}
		
		
			, member_email = #{member_email}  					
		
		
			, member_like_genre = #{member_like_genre}
		
	WHERE member_id = #{member_id}
