mapper.insert:
INSERT
	INTO LIKES
		(
		member_id
		, movie_num
		)
VALUES
		(
		#{member_id}
		, #{movie_num}
		)

mapper.delete:
DELETE
	FROM LIKES
WHERE
	member_id = #{member_id}
	AND movie_num = ${movie_num}

mapper.select:
SELECT COUNT(*)
	FROM LIKES
	WHERE member_id = #{member_id}
